import Ember from 'ember';
import FormController from '../mixins/form-controller';
var schoolsController;

schoolsController = Ember.ArrayController.extend(FormController, {
  itemController: "school",
  sortProperties: ['createdAt'],
  sortAscending: false,
  newSchool: Ember.Object.create({
    name: '',
    type: '',
    city: '',
    state: ''
  }),
  schoolTypes: (function() {
    return this.store.find("school-type");
  }).property(),
  schoolStates: (function() {
    return this.store.find("state");
  }).property(),
  modalButtons: [
    Ember.Object.create({
      title: 'Cancelar',
      clicked: "cancel",
      dismiss: 'modal'
    }), Ember.Object.create({
      title: 'Guardar',
      type: "primary",
      clicked: "submit",
      id: "btn_saveNewSchool"
    })
  ],
  actions: {
    addSchool: function() {
      console.log("Add School!!");
      return Bootstrap.ModalManager.show('schoolModal');
    },
    submit: function() {
      var isFormValid, school, schoolState, schoolType,
        _this = this;
      isFormValid = this.get('isFormValid');
      if (isFormValid) {
        schoolType = this.newSchool.type;
        schoolState = this.newSchool.state;
        school = this.store.createRecord('school', {
          name: this.newSchool.name,
          city: this.newSchool.city
        });
        school.save().then(function() {
          _this.store.find('school-type', schoolType).then(function(type) {
            return school.set('type', type);
          });
          return _this.store.find('state', schoolState).then(function(state) {
            return school.set('state', state);
          });
        });
        this.set('newSchool.name', '');
        this.set('newSchool.type', '');
        this.set('newSchool.city', '');
        this.set('newSchool.state', '');
        return Bootstrap.ModalManager.hide('schoolModal', function(error) {
          school.rollback();
          return alert(error);
        });
      }
    },
    cancel: function() {
      console.log("Cancel!!");
      this.set('newSchool.name', '');
      this.set('newSchool.type', '');
      this.set('newSchool.city', '');
      return this.set('newSchool.state', '');
    }
  }
});

